import React, { useState } from 'react';

const UpdateFlightStatus = () => {
  const [status, setStatus] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setStatus(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/flights/6E2341', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        throw new Error('Failed to update flight status');
      }

      setMessage('Flight status updated successfully!');
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const mainStyle = {
    fontSize: '96px', // 24px * 4
  };

  const buttonStyle = {
    fontSize: '24px', // Smaller font size for the button
  };

  const fonts = [
    { fontFamily: 'Arial', label: 'Arial' },
    { fontFamily: 'Georgia', label: 'Georgia' },
    { fontFamily: 'Courier New', label: 'Courier New' },
    { fontFamily: 'Times New Roman', label: 'Times New Roman' },
    { fontFamily: 'Verdana', label: 'Verdana' },
  ];

  return (
    <div>
      <div style={mainStyle}>
        <h2>Update Flight Status</h2>
        <form onSubmit={handleSubmit}>
          <label style={mainStyle}>
            Flight Status: 
            <br />
            <br />
            <input 
              type="text" 
              value={status} 
              onChange={handleChange} 
              style={{ fontSize: '24px' }} // Keeping the font size of the textbox as 24px
            />
          </label>
          <br />
          <br />
          <button type="submit" style={buttonStyle}>Update Status</button>
        </form>
        {message && <p style={mainStyle}>{message}</p>}
      </div>

      <div style={{ border: '1px solid #ccc', padding: '20px', marginTop: '20px' }}>
        <h3>Font Styles</h3>
        {fonts.map((font, index) => (
          <div key={index} style={{ fontFamily: font.fontFamily, marginBottom: '10px' }}>
            <p style={{ fontSize: '24px' }}>{font.label} - This is a sample text</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UpdateFlightStatus;
