const handleSubmit = async (event) => {
    event.preventDefault();
  
    try {
      const response = await fetch('http://localhost:8080/api/flights/6E2341', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });
  
      if (!response.ok) {
        const errorData = await response.json(); // Extract response body for error details
        throw new Error(`Failed to update flight status: ${response.statusText} - ${JSON.stringify(errorData)}`);
      }
  
      setMessage('Flight status updated successfully!');
    } catch (error) {
      console.error('Fetch error:', error);
      setMessage(`Error: ${error.message}`);
    }
  };
