import React, { useState } from 'react';

const UpdateFlightStatus = () => {
  const [status, setStatus] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setStatus(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/flights/6E2341', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        throw new Error('Failed to update flight status');
      }

      setMessage('Flight status updated successfully!');
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const mainStyle = {
    fontSize: '38.4px', // 19.2px * 2
  };

  const buttonStyle = {
    fontSize: '9.6px', // 4.8px * 2
  };

  return (
    <div style={mainStyle}>
      <h2>Update Flight Status</h2>
      <form onSubmit={handleSubmit}>
        <label style={mainStyle}>
          Flight Status: 
          <br></br>
          <br></br>
          <input 
            type="text" 
            value={status} 
            onChange={handleChange} 
            style={{ fontSize: '9.6px' }} // Keeping the font size of the textbox as 9.6px
          />
        </label>
        <br></br>
        <br></br>
        <button type="submit" style={buttonStyle}>Update Status</button>
      </form>
      {message && <p style={mainStyle}>{message}</p>}
    </div>
  );
};

export default UpdateFlightStatus;
