import React, { useState } from 'react';

const UpdateFlightStatus = () => {
  const [status, setStatus] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setStatus(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/flights/6E2341', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        throw new Error('Failed to update flight status');
      }

      setMessage('Flight status updated successfully!');
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const style = {
    fontSize: '24px', // Font size for everything except textbox
  };

  const textboxStyle = {
    fontSize: '12px', // Font size for textbox
  };

  return (
    <div style={style}>
      <h2>Update Flight Status</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Flight Status: 
          <br />
          <br />
          <input 
            type="text" 
            value={status} 
            onChange={handleChange} 
            style={textboxStyle}
          />
        </label>
        <br />
        <br />
        <button type="submit">Update Status</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default UpdateFlightStatus;
