import React, { useState } from 'react';

function App() {
  const [flightStatus, setFlightStatus] = useState('');

  const handleInputChange = (event) => {
    setFlightStatus(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // You would typically make an API call here to get flight status
    // based on the entered flight number. 
    // For example:
    // fetch(`https://api.example.com/flights/${flightStatus}`)
    //   .then(response => response.json())
    //   .then(data => {
    //     // Update the UI with the flight status data
    //   });
    console.log('Submitted flight status:', flightStatus);
  };

  return (
    <div className="container">
      <h1>Flight Status</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="flightNumber">Flight ID:</label>
          <input
            type="text"
            className="form-control"
            id="flightNumber"
            value={flightStatus}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit" className="btn-primary">
          Show Details
        </button>
      </form>
      {/* Display flight status information here */}
    </div>
  );
}

export default App;
