import React, { useState } from 'react';

const UpdateFlightStatus = () => {
  const [status, setStatus] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setStatus(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/flights/6E2341', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        throw new Error('Failed to update flight status');
      }

      setMessage('Flight status updated successfully!');
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const styles = {
    container: {
      fontSize: '18px',
    },
    label: {
      fontSize: '18px',
    },
    input: {
      fontSize: '16px',
    },
    button: {
      fontSize: '16px',
    },
    message: {
      fontSize: '18px',
      color: 'green',
    },
  };

  return (
    <div style={styles.container}>
      <h2>Update Flight Status</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>
          Flight Status: 
          <br></br>
          <br></br>
          <input type="text" value={status} onChange={handleChange} style={styles.input} />
        </label>
        <br></br>
        <br></br>
        <button type="submit" style={styles.button}>Update Status</button>
      </form>
      {message && <p style={styles.message}>{message}</p>}
    </div>
  );
};

export default UpdateFlightStatus;
