  //       setBackendData(data);
  //     } catch (error) {
  //       setError(error.message);
  //     } finally {
  //       setLoading(false);
  //     }
  //   };

  //   fetchData();
  // }, [id]);

  if (loading) {
    return <p>Loading...</p>;
  }

