import React, { useState } from 'react';

const UpdateFlightStatus = () => {
  const [status, setStatus] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setStatus(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/api/flights/6E2341', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        throw new Error('Failed to update flight status');
      }

      setMessage('Flight status updated successfully!');
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  const fontSizeStyle = { fontSize: '2.5em' }; // 5 times larger than the default size

  return (
    <div>
      <h2 style={fontSizeStyle}>Update Flight Status</h2>
      <form onSubmit={handleSubmit}>
        <label style={fontSizeStyle}>
          Flight Status: 
          <br />
          <br />
          <input 
            type="text" 
            value={status} 
            onChange={handleChange} 
            style={fontSizeStyle} 
          />
        </label>
        <br />
        <br />
        <button type="submit" style={fontSizeStyle}>Update Status</button>
      </form>
      {message && <p style={fontSizeStyle}>{message}</p>}
    </div>
  );
};

export default UpdateFlightStatus;
