import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Status() {
  const { id } = useParams();
  const [backendData, setBackendData] = useState([{"flightId":"6E 2341","airline":"Indigo","status":"Cancelled","departureGate":"A12","arrivalGate":"B7","scheduledDeparture":"2024-07-26 14:00:00","scheduledArrival":"2024-07-26 18:00:00","actualDeparture":null,"actualArrival":null,"updated":0}]);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);


  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      <h1>Flight Status for ID: {id}</h1>
      <ul>
        {backendData.map(flight => (
          <li key={flight.id}>
            {flight.flightId} - {flight.status}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Status;
